select.if:
SELECT province,device_count,proportion FROM stat_device_location
WHERE sys_user_id = #{sysUserId}
AND date_format(ctime,'%Y-%m-%d') = date_format(#{date},'%Y-%m-%d')
AND product_id = #{productId}

mapper.select:
SELECT province,SUM(device_count) as device_count FROM stat_device_location
WHERE date_format(ctime,'%Y-%m-%d') = date_format(#{date},'%Y-%m-%d')

    AND product_id = #{productId}


    AND sys_user_id IN

        #{item}


GROUP BY province

select.if:
SELECT province,device_count,proportion FROM stat_device_location
WHERE sys_user_id = #{sysUserId}
AND date_format(ctime,'%Y-%m-%d') = date_format(#{date},'%Y-%m-%d')
AND product_id = #{productId}
ORDER BY device_count DESC
limit 0,10

mapper.select:
SELECT province,SUM(device_count) as device_count,avg(proportion) as proportion FROM stat_device_location WHERE date_format(ctime,'%Y-%m-%d') = date_format(#{date},'%Y-%m-%d')
AND product_id = #{productId}
AND sys_user_id IN
#{item}
GROUP BY province ORDER BY device_count DESC limit 0,10
