mapper.update:
UPDATE stat_user_widget SET total_count = #{widget.totalCount} ,active_count =#{widget.activeCount},utime=#{widget.utime}
WHERE sys_user_id =#{widget.sysUserId}
AND DATE_FORMAT(utime,'%Y-%m-%d') = DATE_FORMAT(#{widget.utime},'%Y-%m-%d')

mapper.select:
select sum(total_count) total_count, sum(new_count) new_count, sum(active_count) active_count from stat_user_widget where date_format(utime,'%Y-%m-%d') = date_format(#{date},'%Y-%m-%d')
AND sys_user_id IN
#{item}
